import React from "react";
import "./About.css";

function About() {
    const [activeTab, setActiveTab] = useState("bio");

    return (
      <div className="tabs">
        <button 
          onClick={() => setActiveTab("bio")}
          className={activeTab === "bio" ? "active" : ""}
        >
          عني
        </button>
        <button 
          onClick={() => setActiveTab("hobbies")}
          className={activeTab === "hobbies" ? "active" : ""}
        >
          هواياتي
        </button>
        
        {activeTab === "bio" ? (
          <p>...النص الشخصي...</p>
        ) : (
          <div className="hobbies-grid">...</div>
        )}
      </div>
    );
}

export default About;
